mapper.select:
select    from grow_flow where  
1=1 

	and user_id = #{gfq.userId}


	and event_code = #{gfq.eventCode}


	and create_time >= #{gfq.startTime}


	and create_time <= #{gfq.endTime}

select  count(1)  
 from (
select   
 from grow_flow where 
1=1 

	and user_id = #{gfq.userId}


	and event_code = #{gfq.eventCode}


	and create_time >= #{gfq.startTime}


	and create_time <= #{gfq.endTime}

) a 
left join event_sys b on a.eventCode = b.event_code 
order by a.id desc

select 
		a.id  ,
		a.userId,  
		a.eventCode ,
		a.newGrow ,
		a.allGrow ,
		a.createTime  ,
		a.updateTime ,
		b.event_name AS eventName
 from (
select    from grow_flow where 
1=1 

	and user_id = #{gfq.userId} 


	and event_code = #{gfq.eventCode}


	and create_time >= #{gfq.startTime}


	and create_time <= #{gfq.endTime}

) a 
left join event_sys b on a.eventCode = b.event_code 
order by id desc

mapper.update:
UPDATE grow_flow 


		new_grow = #{newGrow},


		all_grow = #{allGrow},

	update_time = #{updateTime}

where user_id = #{userId} and event_code=#{eventCode}
