mapper.select:
		select   
			 count(1)
  	    from (
		select  from score_flow where user_id = #{sfq.userId}

			and consume_flag = #{sfq.consumeFlag}


			and event_code = #{sfq.eventCode}


			and create_time >= #{sfq.startTime}


			and create_time <= #{sfq.endTime}

		) a left join event_sys b on a.eventCode = b.event_code
		order by a.id desc

		select   
			a.id  ,
			a.userId  ,
			a.eventCode  ,
			a.newScore  ,
			a.allScore  ,
			a.consumeFlag  ,
			a.createTime  ,
			a.updateTime  ,
			b.event_name  as eventName 
  	  from (
		select  from score_flow where user_id = #{sfq.userId}

			and consume_flag = #{sfq.consumeFlag}


			and event_code = #{sfq.eventCode}


			and create_time >= #{sfq.startTime}


			and create_time <= #{sfq.endTime}

		) a left join event_sys b on a.eventCode = b.event_code
		order by id desc

mapper.update:
UPDATE score_flow


		new_score = #{newScore},


		all_score = #{allScore},

	update_time = #{updateTime}

where user_id = #{userId} and event_code=#{eventCode}
