#{kid,jdbcType=BIGINT}
,#{categoryName,jdbcType=VARCHAR}
,#{parentKid,jdbcType=BIGINT}
,#{recommend,jdbcType=TINYINT}
,#{categorySort,jdbcType=TINYINT}
