mapper.select:
select user_inviter_code
from qh_user_operate_info
where user_id = #{userId}

select cust_reg_inviter_num
from qh_user_operate_info
where user_id = #{userId}

select user_id
from qh_user_operate_info
where user_inviter_code = #{userInviterCode}

select
kid as kid,
user_id as userId,
create_date as createDate
from qh_user_operate_info
where user_inviter_id = #{userInviterId}

	and kid < #{kid}

order by create_date desc
limit #{limit}

select


from qh_user_operate_info a
where 1=1

	and a.user_id = #{userId}


	and a.user_inviter_id = #{userInviterId}


	and a.create_date > #{startDate}


	and a.create_date < #{endDate}

order by a.create_date desc
