mapper.update:
UPDATE qh_user_relation SET

create_date= NOW()
,create_user_id = #{createUserId,jdbcType=VARCHAR}
,last_update_date = NOW()
,last_update_user_id = #{lastUpdateUserId,jdbcType=VARCHAR}

,black_status  = #{blackStatus,jdbcType=TINYINT}
,follow_status = #{followStatus,jdbcType=TINYINT}
,friend_status = #{friendStatus,jdbcType=TINYINT}

WHERE kid = #{kid,jdbcType=BIGINT}
AND source_user_id = #{sourceUserId,jdbcType=VARCHAR}
AND target_user_id = #{targetUserId,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM qh_user_relation WHERE
del_flag = 10 AND



        (source_user_id = #{sourceUserId,jdbcType=VARCHAR}

        AND target_user_id IN

            #{userId,jdbcType=VARCHAR}


        ) OR (

        target_user_id = #{sourceUserId,jdbcType=VARCHAR}
        AND source_user_id IN

            #{userId,jdbcType=VARCHAR}


        )


        FALSE

SELECT

FROM qh_user_relation WHERE
del_flag = 10 AND


        target_user_id = #{userId,jdbcType=VARCHAR}
        AND follow_status = 11


        source_user_id = #{userId,jdbcType=VARCHAR}
        AND follow_status = 11


        source_user_id = #{userId,jdbcType=VARCHAR}
        AND black_status = 11


        target_user_id = #{userId,jdbcType=VARCHAR}
        AND black_status = 11


        source_user_id = #{userId,jdbcType=VARCHAR}
        AND friend_status = 11


        FALSE

SELECT
COUNT(1) AS totalCount
FROM qh_user_relation WHERE
del_flag = 10


        AND target_user_id = #{userId,jdbcType=VARCHAR}
        AND follow_status = 11


        AND source_user_id = #{userId,jdbcType=VARCHAR}
        AND follow_status = 11


        AND source_user_id = #{userId,jdbcType=VARCHAR}
        AND black_status = 11


        AND target_user_id = #{userId,jdbcType=VARCHAR}
        AND black_status = 11


        AND source_user_id = #{userId,jdbcType=VARCHAR}
        AND friend_status = 11


        AND FALSE
