mapper.select:
select

from qh_user_baseinfo 
where app_id = #{appId}  and del_flag = 10 and user_status != 12

	and user_nick_name = #{nickName}


	and user_phone = #{userPhone}

select
from qh_user_baseinfo where del_flag = 10 and user_status != 12 and user_id in
#{item}

select
user_id as userId
from qh_user_baseinfo 
where app_id = #{appId} and del_flag = 10 and user_status != 12

	and user_nick_name like CONCAT('%',#{nickName},'%')


	and user_phone like CONCAT('%',#{phone},'%')


	and create_date >= #{startDate}


	and create_date <= #{endDate}


	and user_role = #{userRole}

select
a.user_id as userId
from qh_user_baseinfo 
where app_id = #{appId} and del_flag = 10 and user_status != 12


	and user_nick_name = #{nickName}


	and user_phone = #{phone}


	and create_date >= #{startDate}


	and create_date <= #{endDate}

select
user_id as userId
from qh_user_baseinfo a
where a.create_date >= #{startDate}
	and a.create_date <= #{endDate}

select
from qh_user_baseinfo where user_id in
#{item}
